import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <div className="logo">
            <Link to="/">
              <span style={{ color: '#e85504', fontWeight: 'bold', fontSize: '1.5rem' }}>TruPath</span>
            </Link>
          </div>
          
          <button 
            className="mobile-menu-btn" 
            onClick={toggleMobileMenu}
            style={{ background: 'none', border: 'none', fontSize: '1.5rem', cursor: 'pointer' }}
          >
            {isMobileMenuOpen ? '✕' : '☰'}
          </button>
          
          <nav className={`nav-links ${isMobileMenuOpen ? 'active' : ''}`}>
            <Link to="/" onClick={() => setIsMobileMenuOpen(false)}>Home</Link>
            <Link to="/about" onClick={() => setIsMobileMenuOpen(false)}>About Us</Link>
            <Link to="/dealer-locator" onClick={() => setIsMobileMenuOpen(false)}>Dealer Locator</Link>
            <Link to="/become-dealer" onClick={() => setIsMobileMenuOpen(false)}>Become a Dealer</Link>
            <Link to="/contact" onClick={() => setIsMobileMenuOpen(false)}>Contact Us</Link>
          </nav>
        </div>
      </div>
    </header>
  );
};

export default Header;
